mapper.select:
select
id,
name as article_name,
description,
content,
author_id,
create_time,
last_time,
status as article_status,
p.author_name as author_name
from post_article
where
id = #{id}

    and name = #{articleName}


    and author_id = #{authorId}


    and create_time = #{createTime}


    and last_time = #{lastTime}


    and status = #{articleStatus}

;
